import * as dynamodb from 'aws-cdk-lib/aws-dynamodb';
import * as efs from 'aws-cdk-lib/aws-efs';
import { App, Duration, RemovalPolicy, Stack, StackProps } from 'aws-cdk-lib';
import { Construct } from 'constructs';
import * as backup from 'aws-cdk-lib/aws-backup';

class TestStack extends Stack {
  constructor(scope: Construct, id: string, props?: StackProps) {
    super(scope, id, props);

    new dynamodb.Table(this, 'Table', {
      partitionKey: {
        name: 'id',
        type: dynamodb.AttributeType.STRING,
      },
      removalPolicy: RemovalPolicy.DESTROY,
    });

    const fs = new efs.CfnFileSystem(this, 'FileSystem');
    fs.applyRemovalPolicy(RemovalPolicy.DESTROY);

    const vault = new backup.BackupVault(this, 'Vault', {
      removalPolicy: RemovalPolicy.DESTROY,
      lockConfiguration: {
        minRetention: Duration.days(5),
      },
    });
    const secondaryVault = new backup.BackupVault(this, 'SecondaryVault', {
      removalPolicy: RemovalPolicy.DESTROY,
      lockConfiguration: {
        minRetention: Duration.days(5),
      },
    });
    const plan = backup.BackupPlan.dailyWeeklyMonthly5YearRetention(this, 'Plan', vault);

    plan.addSelection('Selection', {
      resources: [
        backup.BackupResource.fromConstruct(this), // All backupable resources in this construct
        backup.BackupResource.fromTag('stage', 'prod'), // Resources that are tagged stage=prod
      ],
    });

    plan.addRule(new backup.BackupPlanRule({
      copyActions: [{
        destinationBackupVault: secondaryVault,
        moveToColdStorageAfter: Duration.days(30),
        deleteAfter: Duration.days(120),
      }],
      recoveryPointTags: {
        stage: 'prod',
      },
    }));
  }
}

const app = new App();
new TestStack(app, 'cdk-backup');
app.synth();

import * as ec2 from 'aws-cdk-lib/aws-ec2';
import * as elbv2 from 'aws-cdk-lib/aws-elasticloadbalancingv2';
import * as ga from 'aws-cdk-lib/aws-globalaccelerator';
import { App, Stack } from 'aws-cdk-lib';
import * as constructs from 'constructs';
import * as endpoints from 'aws-cdk-lib/aws-globalaccelerator-endpoints';

class GaStack extends Stack {
  constructor(scope: constructs.Construct, id: string) {
    super(scope, id);

    const vpc = new ec2.Vpc(this, 'VPC', { maxAzs: 3, natGateways: 1, restrictDefaultSecurityGroup: false });
    const accelerator = new ga.Accelerator(this, 'Accelerator');
    const listener = new ga.Listener(this, 'Listener', {
      accelerator,
      portRanges: [
        {
          fromPort: 80,
          toPort: 80,
        },
      ],
    });
    const alb = new elbv2.ApplicationLoadBalancer(this, 'ALB', { vpc, internetFacing: true });
    const nlb = new elbv2.NetworkLoadBalancer(this, 'NLB', { vpc, internetFacing: true });
    const eip = new ec2.CfnEIP(this, 'ElasticIpAddress');
    const instances = new Array<ec2.Instance>();

    for ( let i = 0; i < 2; i++) {
      instances.push(new ec2.Instance(this, `Instance${i}`, {
        vpc,
        machineImage: new ec2.AmazonLinuxImage(),
        instanceType: new ec2.InstanceType('t3.small'),
      }));
    }

    const group = new ga.EndpointGroup(this, 'Group', {
      listener,
      endpoints: [
        new endpoints.ApplicationLoadBalancerEndpoint(alb),
        new endpoints.NetworkLoadBalancerEndpoint(nlb),
        new endpoints.CfnEipEndpoint(eip),
        new endpoints.InstanceEndpoint(instances[0]),
        new endpoints.InstanceEndpoint(instances[1]),
      ],
    });

    alb.connections.allowFrom(group.connectionsPeer('Peer', vpc), ec2.Port.tcp(443));
  }
}

const app = new App();
new GaStack(app, 'integ-globalaccelerator');

#!/usr/bin/env node
import * as ec2 from 'aws-cdk-lib/aws-ec2';
import * as cdk from 'aws-cdk-lib';
import * as elbv2 from 'aws-cdk-lib/aws-elasticloadbalancingv2';

const app = new cdk.App();
const stack = new cdk.Stack(app, 'aws-cdk-elbv2-integ');

const vpc = new ec2.Vpc(stack, 'VPC', {
  restrictDefaultSecurityGroup: false,
  maxAzs: 2,
});

const lb = new elbv2.ApplicationLoadBalancer(stack, 'LB', {
  vpc,
  internetFacing: true,
});

const listener = lb.addListener('Listener', {
  port: 80,
});

const group1 = listener.addTargets('Target', {
  port: 80,
  targets: [new elbv2.IpTarget('10.0.128.4')],
});

const group2 = listener.addTargets('ConditionalTarget', {
  priority: 10,
  hostHeader: 'example.com',
  port: 80,
  targets: [new elbv2.IpTarget('10.0.128.5')],
});

listener.addAction('action1', {
  priority: 1,
  conditions: [
    elbv2.ListenerCondition.hostHeaders(['example.com']),
  ],
  action: elbv2.ListenerAction.fixedResponse(200, { messageBody: 'success' }),
});

group1.metricTargetResponseTime().createAlarm(stack, 'ResponseTimeHigh1', {
  threshold: 5,
  evaluationPeriods: 2,
});

group2.metricTargetResponseTime().createAlarm(stack, 'ResponseTimeHigh2', {
  threshold: 5,
  evaluationPeriods: 2,
});

