import * as codebuild from 'aws-cdk-lib/aws-codebuild';
import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as ec2 from 'aws-cdk-lib/aws-ec2';
import * as ecr from 'aws-cdk-lib/aws-ecr';
import * as ecs from 'aws-cdk-lib/aws-ecs';
import * as s3 from 'aws-cdk-lib/aws-s3';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

/* eslint-disable quote-props */

const app = new cdk.App();

const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-ecs-deploy');

const vpc = new ec2.Vpc(stack, 'VPC', {
  restrictDefaultSecurityGroup: false,
  maxAzs: 1,
});
const cluster = new ecs.Cluster(stack, 'EcsCluster', {
  vpc,
});
const repository = new ecr.Repository(stack, 'EcrRepo');
const taskDefinition = new ecs.FargateTaskDefinition(stack, 'TaskDef');
const containerName = 'Container';
taskDefinition.addContainer(containerName, {
  image: ecs.ContainerImage.fromRegistry('amazon/amazon-ecs-sample'),
});
const service = new ecs.FargateService(stack, 'FargateService', {
  cluster,
  taskDefinition,
});

const bucket = new s3.Bucket(stack, 'MyBucket', {
  versioned: true,
  removalPolicy: cdk.RemovalPolicy.DESTROY,
});
const sourceOutput = new codepipeline.Artifact('SourceArtifact');
const sourceAction = new cpactions.S3SourceAction({
  actionName: 'Source',
  output: sourceOutput,
  bucket,
  bucketKey: 'path/to/Dockerfile',
});

const project = new codebuild.PipelineProject(stack, 'EcsProject', {
  environment: {
    buildImage: codebuild.LinuxBuildImage.UBUNTU_14_04_DOCKER_17_09_0,
    privileged: true,
  },
  buildSpec: codebuild.BuildSpec.fromObject({
    version: '0.2',
    phases: {
      pre_build: {
        commands: '$(aws ecr get-login --region $AWS_DEFAULT_REGION --no-include-email)',
      },
      build: {
        commands: 'docker build -t $REPOSITORY_URI:latest .',
      },
      post_build: {
        commands: [
          'docker push $REPOSITORY_URI:latest',
          `printf '[{ "name": "${containerName}", "imageUri": "%s" }]' $REPOSITORY_URI:latest > imagedefinitions.json`,
        ],
      },
    },
    artifacts: {
      files: 'imagedefinitions.json',
    },
  }),
  environmentVariables: {
    'REPOSITORY_URI': {
      value: repository.repositoryUri,
    },
  },
  grantReportGroupPermissions: false,
});
// needed for `docker push`
repository.grantPullPush(project);
const buildOutput = new codepipeline.Artifact();
const buildAction = new cpactions.CodeBuildAction({
  actionName: 'CodeBuild',
  project,
  input: sourceOutput,
  outputs: [buildOutput],
});

new codepipeline.Pipeline(stack, 'MyPipeline', {
  artifactBucket: bucket,
  stages: [
    {
      stageName: 'Source',
      actions: [sourceAction],
    },
    {
      stageName: 'Build',
      actions: [buildAction],
    },
    {
      stageName: 'Deploy',
      actions: [
        new cpactions.EcsDeployAction({
          actionName: 'DeployAction',
          input: buildOutput,
          service,
          deploymentTimeout: cdk.Duration.minutes(60),
        }),
      ],
    },
  ],
});

app.synth();

import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as s3 from 'aws-cdk-lib/aws-s3';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

const app = new cdk.App();

const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-jenkins');

const bucket = new s3.Bucket(stack, 'MyBucket', {
  versioned: true,
});

const pipeline = new codepipeline.Pipeline(stack, 'Pipeline', {
  artifactBucket: bucket,
});

const sourceOutput = new codepipeline.Artifact();
const sourceAction = new cpactions.S3SourceAction({
  actionName: 'S3',
  bucketKey: 'some/path',
  bucket,
  output: sourceOutput,
});
pipeline.addStage({
  stageName: 'Source',
  actions: [sourceAction],
});

const jenkinsProvider = new cpactions.JenkinsProvider(stack, 'JenkinsProvider', {
  providerName: 'JenkinsProvider',
  serverUrl: 'http://myjenkins.com:8080',
  version: '2',
});

pipeline.addStage({
  stageName: 'Build',
  actions: [
    new cpactions.JenkinsAction({
      actionName: 'JenkinsBuild',
      jenkinsProvider,
      type: cpactions.JenkinsActionType.BUILD,
      projectName: 'JenkinsProject1',
      inputs: [sourceOutput],
      outputs: [new codepipeline.Artifact()],
    }),
    new cpactions.JenkinsAction({
      actionName: 'JenkinsTest',
      jenkinsProvider,
      type: cpactions.JenkinsActionType.TEST,
      projectName: 'JenkinsProject2',
      inputs: [sourceOutput],
    }),
    new cpactions.JenkinsAction({
      actionName: 'JenkinsTest2',
      jenkinsProvider,
      type: cpactions.JenkinsActionType.TEST,
      projectName: 'JenkinsProject3',
      inputs: [sourceOutput],
    }),
  ],
});

app.synth();

import * as codedeploy from 'aws-cdk-lib/aws-codedeploy';
import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as s3 from 'aws-cdk-lib/aws-s3';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

const app = new cdk.App();

const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-codedeploy-ecs');

const application = codedeploy.EcsApplication.fromEcsApplicationName(stack, 'CodeDeployApplication', 'IntegTestDeployApp');

const deploymentGroup = codedeploy.EcsDeploymentGroup.fromEcsDeploymentGroupAttributes(stack, 'CodeDeployGroup', {
  application,
  deploymentGroupName: 'IntegTestDeploymentGroup',
});

const bucket = new s3.Bucket(stack, 'CodeDeployPipelineIntegTest', {
  versioned: true,
  removalPolicy: cdk.RemovalPolicy.DESTROY,
});

const pipeline = new codepipeline.Pipeline(stack, 'Pipeline', {
  artifactBucket: bucket,
});

const sourceStage = pipeline.addStage({ stageName: 'Source' });
const sourceOutput = new codepipeline.Artifact('SourceOutput');
const sourceAction = new cpactions.S3SourceAction({
  actionName: 'S3Source',
  bucketKey: 'application.zip',
  output: sourceOutput,
  bucket,
});
sourceStage.addAction(sourceAction);

const deployStage = pipeline.addStage({ stageName: 'Deploy' });
deployStage.addAction(new cpactions.CodeDeployEcsDeployAction({
  actionName: 'CodeDeploy',
  deploymentGroup,
  taskDefinitionTemplateFile: new codepipeline.ArtifactPath(sourceOutput, 'task-definition-test.json'),
  appSpecTemplateFile: new codepipeline.ArtifactPath(sourceOutput, 'appspec-test.json'),
  containerImageInputs: [
    {
      input: sourceOutput,
      taskDefinitionPlaceholder: 'PLACEHOLDER',
    },
  ],
}));

app.synth();

import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as s3 from 'aws-cdk-lib/aws-s3';
import * as stepfunctions from 'aws-cdk-lib/aws-stepfunctions';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

const app = new cdk.App();

const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-stepfunctions');

const sourceOutput = new codepipeline.Artifact();

const startState = new stepfunctions.Pass(stack, 'StartState');
const simpleStateMachine = new stepfunctions.StateMachine(stack, 'SimpleStateMachine', {
  definition: startState,
});

const pipeline = new codepipeline.Pipeline(stack, 'MyPipeline');
pipeline.addStage({
  stageName: 'Source',
  actions: [
    new cpactions.S3SourceAction({
      actionName: 'Source',
      bucket: new s3.Bucket(stack, 'MyBucket'),
      bucketKey: 'some/path/to',
      output: sourceOutput,
      trigger: cpactions.S3Trigger.POLL,
    }),
  ],
});
pipeline.addStage({
  stageName: 'Invoke',
  actions: [
    new cpactions.StepFunctionInvokeAction({
      actionName: 'Invoke',
      stateMachine: simpleStateMachine,
      stateMachineInput: cpactions.StateMachineInput.literal({ IsHelloWorldExample: true }),
    }),
  ],
});

app.synth();

import * as codebuild from 'aws-cdk-lib/aws-codebuild';
import * as codecommit from 'aws-cdk-lib/aws-codecommit';
import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as s3 from 'aws-cdk-lib/aws-s3';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

const app = new cdk.App();

const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-codebuild-multiple-inputs-outputs');

const repository = new codecommit.Repository(stack, 'MyRepo', {
  repositoryName: 'MyIntegTestTempRepo',
});
const bucket = new s3.Bucket(stack, 'MyBucket', {
  versioned: true,
  removalPolicy: cdk.RemovalPolicy.DESTROY,
});

const pipeline = new codepipeline.Pipeline(stack, 'Pipeline', {
  artifactBucket: bucket,
});
const pipelineRole = pipeline.role;

const source1Output = new codepipeline.Artifact();
const sourceAction1 = new cpactions.CodeCommitSourceAction({
  actionName: 'Source1',
  repository,
  output: source1Output,
  role: pipelineRole,
});
const source2Output = new codepipeline.Artifact();
const sourceAction2 = new cpactions.S3SourceAction({
  actionName: 'Source2',
  bucketKey: 'some/path',
  bucket,
  output: source2Output,
  role: pipelineRole,
});
pipeline.addStage({
  stageName: 'Source',
  actions: [
    sourceAction1,
    sourceAction2,
  ],
});

const project = new codebuild.PipelineProject(stack, 'MyBuildProject', {
  grantReportGroupPermissions: false,
});
const buildAction = new cpactions.CodeBuildAction({
  actionName: 'Build1',
  project,
  input: source1Output,
  extraInputs: [
    source2Output,
  ],
  outputs: [
    new codepipeline.Artifact(),
    new codepipeline.Artifact(),
  ],
  role: pipelineRole,
});
const testAction = new cpactions.CodeBuildAction({
  type: cpactions.CodeBuildActionType.TEST,
  actionName: 'Build2',
  project,
  input: source2Output,
  extraInputs: [
    source1Output,
  ],
  outputs: [
    new codepipeline.Artifact('CustomOutput2'),
  ],
  role: pipelineRole,
});
pipeline.addStage({
  stageName: 'Build',
  actions: [
    buildAction,
    testAction,
  ],
});

app.synth();

import * as codebuild from 'aws-cdk-lib/aws-codebuild';
import * as codecommit from 'aws-cdk-lib/aws-codecommit';
import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

/* eslint-disable quote-props */

const app = new cdk.App();

const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-codecommit-codebuild');

const repository = new codecommit.Repository(stack, 'MyRepo', {
  repositoryName: 'my-repo',
});
const sourceOutput = new codepipeline.Artifact('SourceArtifact');
const sourceAction = new cpactions.CodeCommitSourceAction({
  actionName: 'source',
  output: sourceOutput,
  repository,
  trigger: cpactions.CodeCommitTrigger.POLL,
});

const project = new codebuild.PipelineProject(stack, 'MyBuildProject', {
  grantReportGroupPermissions: false,
});
const buildAction = new cpactions.CodeBuildAction({
  actionName: 'build',
  project,
  input: sourceOutput,
  outputs: [new codepipeline.Artifact()],
  environmentVariables: {
    'TEST_ENV_VARIABLE': {
      value: 'test env variable value',
    },
    'PARAM_STORE_VARIABLE': {
      value: 'param_store',
      type: codebuild.BuildEnvironmentVariableType.PARAMETER_STORE,
    },
  },
});
const testAction = new cpactions.CodeBuildAction({
  type: cpactions.CodeBuildActionType.TEST,
  actionName: 'test',
  project,
  input: sourceOutput,
});

new codepipeline.Pipeline(stack, 'Pipeline', {
  stages: [
    {
      stageName: 'source',
      actions: [sourceAction],
    },
  ],
}).addStage({
  stageName: 'build',
  actions: [
    buildAction,
    testAction,
  ],
});

app.synth();

import * as codepipeline from 'aws-cdk-lib/aws-codepipeline';
import * as s3 from 'aws-cdk-lib/aws-s3';
import * as cdk from 'aws-cdk-lib';
import * as cpactions from 'aws-cdk-lib/aws-codepipeline-actions';

const app = new cdk.App();

const region = 'us-west-2'; // hardcode the region
const stack = new cdk.Stack(app, 'aws-cdk-codepipeline-cloudformation-cross-region', {
  env: {
    region,
  },
});

const bucket = new s3.Bucket(stack, 'MyBucket', {
  versioned: true,
  removalPolicy: cdk.RemovalPolicy.DESTROY,
});

const sourceOutput = new codepipeline.Artifact();
const sourceAction = new cpactions.S3SourceAction({
  actionName: 'S3',
  bucketKey: 'some/path',
  bucket,
  output: sourceOutput,
});

new codepipeline.Pipeline(stack, 'MyPipeline', {
  artifactBucket: bucket,
  stages: [
    {
      stageName: 'Source',
      actions: [sourceAction],
    },
    {
      stageName: 'CFN',
      actions: [
        new cpactions.CloudFormationCreateUpdateStackAction({
          actionName: 'CFN_Deploy',
          stackName: 'aws-cdk-codepipeline-cross-region-deploy-stack',
          templatePath: sourceOutput.atPath('template.yml'),
          adminPermissions: false,
          region,
        }),
      ],
    },
  ],
});

app.synth();

