import * as ec2 from 'aws-cdk-lib/aws-ec2';
import * as iam from 'aws-cdk-lib/aws-iam';
import * as sns from 'aws-cdk-lib/aws-sns';
import * as cdk from 'aws-cdk-lib';
import * as constructs from 'constructs';
import * as autoscaling from 'aws-cdk-lib/aws-autoscaling';

export class FakeNotificationTarget implements autoscaling.ILifecycleHookTarget {
  constructor(private readonly topic: sns.ITopic) {
  }

  private createRole(scope: constructs.Construct, _role?: iam.IRole) {
    let role = _role;
    if (!role) {
      role = new iam.Role(scope, 'Role', {
        assumedBy: new iam.ServicePrincipal('autoscaling.amazonaws.com'),
      });
    }

    return role;
  }

  public bind(_scope: constructs.Construct, options: autoscaling.BindHookTargetOptions): autoscaling.LifecycleHookTargetConfig {
    const role = this.createRole(options.lifecycleHook, options.role);
    this.topic.grantPublish(role);

    return {
      notificationTargetArn: this.topic.topicArn,
      createdRole: role,
    };
  }
}

export class TestStack extends cdk.Stack {
  constructor(scope: cdk.App, id: string, props?: cdk.StackProps) {
    super(scope, id, props);

    let vpc = new ec2.Vpc(this, 'myVpcAuto', { restrictDefaultSecurityGroup: false });
    const myrole = new iam.Role(this, 'MyRole', {
      assumedBy: new iam.ServicePrincipal('autoscaling.amazonaws.com'),
    });
    const topic = new sns.Topic(this, 'topic', {});
    const topic2 = new sns.Topic(this, 'topic2', {});

    const asg = new autoscaling.AutoScalingGroup(this, 'ASG', {
      vpc,
      instanceType: ec2.InstanceType.of(ec2.InstanceClass.BURSTABLE2, ec2.InstanceSize.MICRO),
      machineImage: new ec2.AmazonLinuxImage(), // get the latest Amazon Linux image
      healthCheck: autoscaling.HealthCheck.ec2(),
    });

    // no role or notificationTarget
    new autoscaling.LifecycleHook(this, 'LCHookNoRoleNoTarget', {
      autoScalingGroup: asg,
      lifecycleTransition: autoscaling.LifecycleTransition.INSTANCE_TERMINATING,
    });

    // no role with notificationTarget
    new autoscaling.LifecycleHook(this, 'LCHookNoRoleTarget', {
      notificationTarget: new FakeNotificationTarget(topic),
      autoScalingGroup: asg,
      lifecycleTransition: autoscaling.LifecycleTransition.INSTANCE_TERMINATING,
    });

    // role with target
    new autoscaling.LifecycleHook(this, 'LCHookRoleTarget', {
      notificationTarget: new FakeNotificationTarget(topic2),
      role: myrole,
      autoScalingGroup: asg,
      lifecycleTransition: autoscaling.LifecycleTransition.INSTANCE_TERMINATING,
    });
  }
}

const app = new cdk.App();

new TestStack(app, 'integ-role-target-hook');

app.synth();

#!/usr/bin/env node
import * as ec2 from 'aws-cdk-lib/aws-ec2';
import * as cdk from 'aws-cdk-lib';
import * as autoscaling from 'aws-cdk-lib/aws-autoscaling';

const app = new cdk.App();
const stack = new cdk.Stack(app, 'aws-cdk-asg-integ');

const lt = new ec2.LaunchTemplate(stack, 'MainLT', {
  instanceType: new ec2.InstanceType('t3.micro'),
  machineImage: new ec2.AmazonLinuxImage({
    generation: ec2.AmazonLinuxGeneration.AMAZON_LINUX_2,
    cpuType: ec2.AmazonLinuxCpuType.X86_64,
  }),
});

const ltOverrideT4g = new ec2.LaunchTemplate(stack, 'T4gLT', {
  instanceType: new ec2.InstanceType('t4g.micro'),
  machineImage: new ec2.AmazonLinuxImage({
    generation: ec2.AmazonLinuxGeneration.AMAZON_LINUX_2,
    cpuType: ec2.AmazonLinuxCpuType.ARM_64,
  }),
});

const vpc = new ec2.Vpc(stack, 'VPC', {
  restrictDefaultSecurityGroup: false,
  maxAzs: 2,
});

new autoscaling.AutoScalingGroup(stack, 'AsgFromLT', {
  vpc,
  launchTemplate: lt,
  minCapacity: 0,
  maxCapacity: 10,
  desiredCapacity: 5,
});

new autoscaling.AutoScalingGroup(stack, 'AsgWithDefaultInstanceWarmup', {
  vpc,
  launchTemplate: lt,
  defaultInstanceWarmup: cdk.Duration.seconds(5),
});

new autoscaling.AutoScalingGroup(stack, 'AsgFromMip', {
  vpc,
  mixedInstancesPolicy: {
    instancesDistribution: {
      onDemandPercentageAboveBaseCapacity: 50,
    },
    launchTemplate: lt,
    launchTemplateOverrides: [
      { instanceType: new ec2.InstanceType('t3.micro') },
      { instanceType: new ec2.InstanceType('t3a.micro') },
      { instanceType: new ec2.InstanceType('t4g.micro'), launchTemplate: ltOverrideT4g },
    ],
  },
  minCapacity: 0,
  maxCapacity: 10,
  desiredCapacity: 5,
});

new autoscaling.AutoScalingGroup(stack, 'AsgFromMipWithoutDistribution', {
  vpc,
  mixedInstancesPolicy: {
    launchTemplate: lt,
    launchTemplateOverrides: [
      { instanceType: new ec2.InstanceType('t3.micro') },
      { instanceType: new ec2.InstanceType('t3a.micro') },
      { instanceType: new ec2.InstanceType('t4g.micro'), launchTemplate: ltOverrideT4g },
    ],
  },
  minCapacity: 0,
  maxCapacity: 10,
  desiredCapacity: 5,
});

new autoscaling.AutoScalingGroup(stack, 'AsgWithGp3Blockdevice', {
  minCapacity: 0,
  maxCapacity: 10,
  desiredCapacity: 5,
  instanceType: ec2.InstanceType.of(ec2.InstanceClass.T3, ec2.InstanceSize.MICRO),
  machineImage: new ec2.AmazonLinuxImage(),
  blockDevices: [{
    deviceName: 'ebs',
    mappingEnabled: true,
    volume: autoscaling.BlockDeviceVolume.ebs(15, {
      deleteOnTermination: true,
      encrypted: true,
      volumeType: autoscaling.EbsDeviceVolumeType.GP3,
      throughput: 125,
    }),
  }],
  vpc,
});

app.synth();

#!/usr/bin/env node
import * as ec2 from 'aws-cdk-lib/aws-ec2';
import * as cdk from 'aws-cdk-lib';
import * as autoscaling from 'aws-cdk-lib/aws-autoscaling';

const app = new cdk.App();
const stack = new cdk.Stack(app, 'aws-cdk-autoscaling-integ');

const vpc = new ec2.Vpc(stack, 'VPC', {
  maxAzs: 2,
  restrictDefaultSecurityGroup: false,
});

const asg = new autoscaling.AutoScalingGroup(stack, 'Fleet', {
  vpc,
  instanceType: ec2.InstanceType.of(ec2.InstanceClass.BURSTABLE2, ec2.InstanceSize.MICRO),
  machineImage: new ec2.AmazonLinuxImage({ generation: ec2.AmazonLinuxGeneration.AMAZON_LINUX_2 }),
});

asg.scaleOnSchedule('ScaleUpInTheMorning', {
  schedule: autoscaling.Schedule.cron({ hour: '8', minute: '0' }),
  minCapacity: 5,
});

asg.scaleOnSchedule('ScaleDownAtNight', {
  schedule: autoscaling.Schedule.cron({ hour: '20', minute: '0' }),
  maxCapacity: 2,
});

asg.scaleOnCpuUtilization('KeepCPUReasonable', {
  targetUtilizationPercent: 50,
});

app.synth();

